  int countUnread() {
    int result = 0;
    for(Map<String, dynamic> i in messages){
      if(!i['is_read']) {
        result += 1;
      }
    }
    return result;
  }

  @override
  void initState() {
    super.initState();
  }

  Stream<bool> checkNotificationPermission() async* {
    while (true) {
      await Future.delayed(Duration.zero);
      bool result = await AwesomeNotifications().isNotificationAllowed();
      yield result;
    }
  }

  @override
  Widget build(BuildContext context) {
    return SafeArea(
      child: Scaffold(
        extendBody: true,
        backgroundColor: Theme.of(context).colorScheme.surface,
        appBar: AppBar(
          backgroundColor: Theme.of(context).colorScheme.surface,
          centerTitle: true,
          title: Text(
            AppLocalizations.of(context)!.notification,
            style: TextStyle(
                color: Theme.of(context).colorScheme.primary,
                fontWeight: FontWeight.bold
            ),
          ),
          leading: IconButton(
              onPressed: () {
                Navigator.pop(context);
              },
              icon: Icon(
                size: 30,
                Icons.arrow_back,
                color: Theme.of(context).colorScheme.primary,
              )
          ),
        ),
        body: StreamBuilder(
          stream: checkNotificationPermission(),
          builder: (context, snapshot) {
            if(snapshot.hasData) {
              if(snapshot.data!) {
                return GroupedListView<dynamic, String>(
                  padding: EdgeInsets.all(10),
                  separator: SizedBox(height: 10,),
                  elements: messages,
                  groupBy: (element) => element['date'],
                  groupComparator: (value1, value2) {
                    DateTime date1 = DateFormat('dd-MM-yyyy').parse(value1);
                    DateTime date2 = DateFormat('dd-MM-yyyy').parse(value2);
                    return date1.compareTo(date2);
                  },
                  order: GroupedListOrder.DESC,
                  groupSeparatorBuilder: (String value) => Padding(
                    padding: const EdgeInsets.fromLTRB(8, 16, 8, 0),
                    child: Text(
                      value,
                      style: TextStyle(
                          fontSize: 20,
                          fontWeight: FontWeight.bold,
                          color: Theme.of(context).colorScheme.primary
                      ),
                    ),
                  ),
                  itemBuilder: (context, dynamic element) => Message(
                    title: element['title'] ?? 'error',
                    detail: element['detail'] ?? 'error',
                    isRead: element['is_read'] ?? false,
                    onTap: () {
                      if(!element['is_read']) {
                        setState(() {
                          element['is_read'] = true;
                        });
                      }
                      Navigator.of(context).push(
                          MaterialPageRoute(builder: (context) => NotificationDetail(
                            title: element['title'] ?? 'error',
                            detail: element['detail'] ?? 'error',
                          ))
                      );
                    },
                    backgroundColor: Theme.of(context).colorScheme.surface,
                    backgroundUnreadColor: Theme.of(context).colorScheme.secondary,
                    borderColor: Theme.of(context).colorScheme.primary,
                    titleColor: Theme.of(context).colorScheme.primary,
                    iconColor: Theme.of(context).colorScheme.primary,
                    detailColor: Theme.of(context).colorScheme.primary,
                  ),
                );
              }
            }
            return Center(
              child: Column(
                crossAxisAlignment: CrossAxisAlignment.center,
                mainAxisAlignment: MainAxisAlignment.center,
                children: [
                  Text(AppLocalizations.of(context)!.notification_permission),
                  ElevatedButton.icon(
                    style: ElevatedButton.styleFrom(
                        backgroundColor: Theme.of(context).colorScheme.primary,
                        foregroundColor: Theme.of(context).colorScheme.tertiary,
                        shape: const RoundedRectangleBorder(
                            borderRadius: BorderRadius.all(Radius.circular(16))
                        )
                    ),
                    onPressed: () {
                      AwesomeNotifications().requestPermissionToSendNotifications();
                    },
                    label: Text(AppLocalizations.of(context)!.setting),
                    icon: Icon(Icons.settings),
                  ),
                ],
              ),
            );
          },
        ),
      ),
    );
  }
}
